/**
 * Tests if the specified path is absolute.
 * @param ***REMOVED***string} path Path to test
 * @returns ***REMOVED***boolean} `true` if path is absolute
 */
export function isAbsolute(path: string): boolean;

/**
 * Normalizes the specified path.
 * @param ***REMOVED***string} path Path to normalize
 * @returns ***REMOVED***string} Normalized path
 */
export function normalize(path: string): string;

/**
 * Resolves the specified include path against the specified origin path.
 * @param ***REMOVED***string} originPath Path to the origin file
 * @param ***REMOVED***string} includePath Include path relative to origin path
 * @param ***REMOVED***boolean} [alreadyNormalized=false] `true` if both paths are already known to be normalized
 * @returns ***REMOVED***string} Path to the include file
 */
export function resolve(originPath: string, includePath: string, alreadyNormalized?: boolean): string;
